import React, { Component } from 'react'
import { SectionList, Image,
	TouchableOpacity, StyleSheet, Text,
    View, Dimensions, ScrollView, Animated, Keyboard } from 'react-native';
import styles from '../../assets/styles/index.js';
// import { KeyboardAwareScrollView } from 'react-native-keyboard-aware-scroll-view'

function AboutScreen () {
    return (
    <ScrollView style={styles.aboutContainer}>
        <View style={styles.aboutHead}>
            {/* <View>
                <TouchableOpacity>Help</TouchableOpacity>
            </View> */}
            <Image 
                source={require('../../assets/logo.svg')}
                    style={styles.appLogo}/>
        </View>
        <View style={styles.aboutBody}>
            <Text style={styles.aboutTitle}>
                About Martha
            </Text>
            <Text>
            MARTHA is an affordable, sustainable, easy-to-use educational tool that can be
            utilized offline. Our proposal is an educational app used via a mobile device, namely an Android
tablet, and corresponding server that can be used by many students. The app will display
different levels, subjects, and lessons to choose from and corresponding files within that
selection, mainly consisting of text material. We will utilize a raspberry pi as our server and local
database which can be updated when intermittently brought to cities with Internet connection.
Within refugee camps, this server can create its own WiFi network for local devices to download
content from.
                {"\n"}{"\n"}
            </Text>
        </View>
    </ScrollView>
  );
}


export default AboutScreen;
